function App() {
  function Big() {
    let time = new Date().toLocaleTimeString();
    // console.log(time);

    const [count, setCount] = useState(time);

    function getTime() {
      setCount(count + 1);
    }

    return getTime();
  }

  return (
    <div className="container">
      <h1>{B}</h1>
      <button onClick={Big}>Get Time</button>
    </div>
  );
}

export default App;
