function App() {
  // console.log(time);

  const [count, setCount] = useState(0);

  function getTime() {
    let time = new Date().toLocaleTimeString();
    const [count, setCount] = useState(0);

    setCount(count + 1);
  }

  function Big() {}

  return (
    <div className="container">
      <h1>{count}</h1>
      <button onClick={getTime}>Get Time</button>
    </div>
  );
}

export default App;
