import React, { useState } from "react";

function App() {
  let time = new Date().toLocaleTimeString();
  console.log(time);

  const [ct, setCt] = useState(0);

  function getTime() {
    setCt((ct = time));
  }

  return (
    <div className="container">
      <h1>{time}</h1>
      <button>Get Time</button>
    </div>
  );
}

export default App;
