import React, { useState } from "react";

function App() {
  let time = new Date().toLocaleTimeString();
  console.log(time);

  // const [ct, setCt] = useState(time);

  function getTime() {
    let currentTime = time;
    return currentTime
  }

  return (
    <div className="container">
      <h1>{getTime()}</h1>
      <button onClick={getTime}>Get Time</button>
    </div>
  );
}

export default App;
